function  Show_definition(Action,strName)% This is the machine-generated representation of a Handle Graphics object% and its children.  Note that handle values may change when these objects% are re-created. This may cause problems with any callbacks written to% depend on the value of the handle at the time the object was saved.%% To reopen this object, just type the name of the M-file at the MATLAB% prompt. The M-file and its associated MAT-file must be on your path.% How to prepare a image file for display in Matlab% 1. Flip the image file ( in image processing software, such as paint shop pro..)% 2. Save the image file in JPEG format% 3. use img = imread('filename.jpg');% 4  save the image file in MATA file format so that the image can be %	 loaded even without the image toolbox save img% 5. load the META file ready for display load imgif nargin == 0 && isempty(findobj('Tag','Definition_dsgn'))% OSU logo page is being initialized    Action = 'initialize';elseif nargin == 0 && ~isempty(findobj('Tag','Definition_dsgn'))		% OSU logo page is already open    Action = 'None';elseif strcmp( Action,'initialize') && ~isempty(findobj('Tag','Definition_dsgn')) % OSU logo page is already open    a = findobj('Tag','Definition_dsgn');    strCurName = get(a,'userdata');    if ~isempty(strmatch(strName,strCurName))        b = strmatch(strName,strCurName);        %   Action = 'None';        figure(a(b));        return    else        Action = 'initialize';    endendif ~strcmp( Action,'initialize')    a = findobj('tag','Definition_dsgn');    strName = get(a,'userdata');endsz  = size(strName);strName_short = strName(1:(sz(2)-4));%strTag = strcat(strName_short,'_dsgn');switch Action      case 'initialize'        ScreenSize = get(0,'ScreenSize');        strCmd = strcat('img = imread(''',strName,''');');        eval(strCmd);        [m,n,~]=size(img);        edg=50;        lim=[edg edg n m];        sx = n+2*edg;        sy = m+2*edg;        FigPos = [ 10 ScreenSize(4)-sy-95 sx sy ] ;                h0 = figure('Color',[0.8 0.8 0.8], ...            'Colormap',gray(256), ...            'IntegerHandle','off', ...            'Interruptible','off', ...            'NumberTitle','off', ...            'Name','Graphics Definitions Window', ...            'Position',FigPos, ...            'Resize','on', ...            'Userdata',strName, ...            'Tag','Definition_dsgn');            h1 = axes('Parent',h0, ...            'Units','pixels', ...            'Box','on', ...            'Color',[1 1 1], ...            'Position',lim, ...            'Tag','Image_axe');                x=160;        y=25;        lim=[0.5*(n-x)+edg 1.5*edg+m-0.7*y x y];        h1 = uicontrol('Parent',h0, ...            'Units','pixels', ...            'BackgroundColor',[0.8 0.8 0.8], ...            'ListboxTop',0, ...            'Position',lim, ...            'String',strName_short, ...            'Style','text', ...            'Tag','StaticText1');        x=100;        y=34;        lim=[0.5*(n-x)+edg 0.5*(edg-y) x y];        h1 = uicontrol('Parent',h0, ...            'Units','pixels', ...            'BackgroundColor',[0.8 0.8 0.8], ...            'Callback','Show_definition(''Close'')', ...            'Position',lim, ...            'String','Close');        image(img);        axis off;        %axis equal;        axis image            case 'None'        a = findobj('tag','Definition_dsgn');        figure(a);       case 'Close'             % if the target window exist then close it          a = findobj('tag','Definition_dsgn');           strCurName = get(a,'userdata');        b = strmatch(strName,strCurName);        close(a(b));end   