function cam2_orf_cam_ana(Action,length,direction,ainc,segments,user_data)% the contents of the userdata of Analysis Window % handles(1) = handle of fig number% handles(2) = handle of fig1 popupmenu% handles(3) = handle of fig2 popupmenu% handles(4) = handle of fig3 popupmenu% handles(6) = handle of axe1% handles(7) = handle of axe2% handles(8) = handle of axe3% handles(10) = flag for deciding to start or stop animation% handles(11) = handle of bead(1)% handles(12) = handle of bead(2)% handles(13) = handle of bead(3)% handles(15) = Previous Start Index% handles(16) = timesink% handles(17) = handle of reference axe for axe1% handles(18) = handle of reference axe for axe2% handles(19) = handle of reference axe for axe3% handles(20) = handle of reference axe for axe4% handles(21) = handle of submenu 'Load'% handles(22) = handle of submenu 'Save'% handles(23) = handle of return button;% handles(24) = handle of speed plus button;% handles(25) = flag1;% each mechanism axes has the its own userdata storing its % unique graphic handle [joint1 joint2 crank coupler rocker];      global strFigType global nAnimationFlagglobal g_AnaResglobal nChangeSpeed% Define the axes for the first graph (input/output angle graph)if nargin==0 && isempty(findobj('Tag','ORF Cam Analysis Window')),  % cogana is being initialized   Action='initialize';elseif nargin== 0 && ~isempty(findobj('Tag','ORF Cam Analysis Window'))      % cogana is already open   Action='None';elseif nargin== 1 && ~isempty(findobj('Tag','ORF Cam Analysis Window')) && strcmp( Action, 'initialize'), % cogana is already open	% Recalculate the value, and then redraw	Action='None';endswitch Actioncase 'initialize'   if  isempty(findobj('Tag','ORF Cam Analysis Window'))         LocalOpenFig;      nAnimationFlag = 0;   else  		cam2_orf_cam_ana('None');   end      if nargin < 7       rb = 2;      r0 = 1;      r1 = 6;      r3 = sqrt(27);      rised = 30;      direction = '-';   	ainc = 5;   end      if exist('nAnimationFlag')       if nAnimationFlag == 1      	cam2_orf_cam_ana('Stop');         return;      end   end   rb = length(1);   rised = length(3);   r1 = length(6);   r0 = length(7);   r3 = length(8);      g_AnaRes = CalcResult( rb,r0,r1,r3,rised,direction,ainc,segments,user_data );   DrawAnalysisWindow;   cam2_orf_cam_ana('ShowFigure');   cam2_orf_cam_ana('Start');    	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	%	Change Figure Call Back Function	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%case 'ChangeFigure'   DrawAnalysisWindow;   cam2_orf_cam_ana('ShowFigure');   cam2_orf_cam_ana('Start');   case 'SetSpeed'   rb = length(1);       nChangeSpeed = rb;   case 'ChangeFigureNo'   DrawAnalysisWindow;   cam2_orf_cam_ana('ShowFigure');   cam2_orf_cam_ana('Start');   case 'Animation'   a = findobj('Tag','ORF Cam Analysis Window');      handles = get(a,'userdata');      [ m, n ] = size( g_AnaRes);   jtotal = g_AnaRes(m,7);   ntotal = g_AnaRes(m,8);   rotinc = g_AnaRes(m,9);   npointf = g_AnaRes(m,10);   itotal = ntotal;   timesink = handles(16);   i = handles(15);   dt = 5;         nFigNum  = get( handles(1),'value');   nType(1) = get( handles(2),'value');   nType(2) = get( handles(3),'value');   nType(3) = get( handles(4),'value');      bead  = zeros(1,4);   links = zeros(4,3);   bead(1) = handles(11);   bead(2) = handles(12);   bead(3) = handles(13);      u = 14;   u = u+1; xca = g_AnaRes(u:u+ntotal-1,1:jtotal);   u = u+ntotal; yca = g_AnaRes(u:u+ntotal-1,1:jtotal);   u = u+ntotal; xstema = g_AnaRes(u:u+1,1:ntotal);   u = u+2; ystema = g_AnaRes(u:u+1,1:ntotal);   xstema = xstema';   ystema = ystema';   u = u+2; xfollower = g_AnaRes(u:u+npointf-1,1:ntotal);   u = u+npointf; yfollower = g_AnaRes(u:u+npointf-1,1:ntotal);   xfollower = xfollower';   yfollower = yfollower';      for j = 1:nFigNum      if nType(j) == 1       	links(j,:) = get(handles(j+5),'userdata');     	end   end      % set timer   tic;   while nAnimationFlag                      cycle=fix((i-0.0000001)/(360/dt)); %determine the cycle as an integer      if i ==1         drawnow;      end               if nFigNum > 0         if nType(1) == 1   			SetMechanismPosition( links(1,:),i,xca,yca,xstema,ystema,xfollower,yfollower);         else         j = nType(1) * 2 -3;         set(bead(1), 'xdata', g_AnaRes(j,i), 'ydata', g_AnaRes(j+1,i));   		end      end               	if nFigNum > 1         if nType(2) == 1   			SetMechanismPosition( links(2,:),i,xca,yca,xstema,ystema,xfollower,yfollower);         else         j = nType(2) * 2 -3;         set(bead(2), 'xdata', g_AnaRes(j,i), 'ydata', g_AnaRes(j+1,i));         end        end         	if nFigNum > 2         if nType(3) == 1   			SetMechanismPosition( links(3,:),i,xca,yca,xstema,ystema,xfollower,yfollower);         else         j = nType(3) * 2 -3;         set(bead(3), 'xdata', g_AnaRes(j,i), 'ydata', g_AnaRes(j+1,i));         end      end            	drawnow;   %flush the draw buffer%      i= i+1;% In cam program, the increment is decided by ainc, a variable given by users% therefore, we setup animation increment = rotinc which is a variable directly% decided by ainc.      i= i+rotinc;      if i >= itotal         i = 1;      end      if nChangeSpeed ~= 0         timesink = timesink * nChangeSpeed;         nChangeSpeed = 0;      end      while toc < timesink      end         tic;   end      nAnimationFlag = 0;   handles(15) = i;   handles(16) = timesink;   set(a,'Userdata',handles);   case 'ShowFigure'   a = findobj('Tag','ORF Cam Analysis Window');   handles = get( a,'userdata');   nFigNum = get(handles(1),'value');   [ m, n ] = size( g_AnaRes);   axislimit = g_AnaRes(m,1:4);   bpoints = g_AnaRes( m,5);   npoints = g_AnaRes( m,6);   jtotal = g_AnaRes( m,7);   ntotal = g_AnaRes( m,8);      u = 6;   u = u+1; xbush1 = g_AnaRes(u,1:bpoints);   u = u+1; ybush1 = g_AnaRes(u,1:bpoints);   u = u+1; xcoordcp = g_AnaRes(u,1:npoints);   u = u+1; ycoordcp = g_AnaRes(u,1:npoints);   u = u+1; xbush2 = g_AnaRes(u,1:bpoints);   u = u+1; ybush2 = g_AnaRes(u,1:bpoints);   u = u+1; xcoordfp = g_AnaRes(u,1:npoints);   u = u+1; ycoordfp = g_AnaRes(u,1:npoints);   qstart = 0;   qstop = 360;   if nFigNum > 0      nType = get(handles(2),'value');      axes(handles(6));      if nType == 1         CreateMechanismHandles( handles(6),axislimit,xcoordcp,ycoordcp,xbush1,ybush1,xcoordfp,ycoordfp,xbush2,ybush2);      else         i = nType * 2 -3;         line1 = line( 'xdata', g_AnaRes(i,1:ntotal), 'ydata', g_AnaRes(i+1,1:ntotal),'tag','line3','color', 'r');   		b1 = line('xdata', [], 'ydata', [],'marker','o','markersize', 6, 'tag','b3');        	handles(11) = b1;   		labels = GetFigLabel( nType-1 );        	xlabel(labels(1));        	ylabel(labels(2));         set(handles(6), 'XTick',[0 60 120 180 240 300 360]);         axis([qstart qstop 0.8*min(g_AnaRes(i+1,:)) 1.2*max(g_AnaRes(i+1,:)) ]);      end   end         if nFigNum > 1      nType = get(handles(3),'value');      axes(handles(7));      if nType == 1         CreateMechanismHandles( handles(7),axislimit,xcoordcp,ycoordcp,xbush1,ybush1,xcoordfp,ycoordfp,xbush2,ybush2);      else         i = nType * 2 -3;         line2 = line( 'xdata', g_AnaRes(i,1:ntotal), 'ydata', g_AnaRes(i+1,1:ntotal),'tag','line3','color', 'b');   		b2 = line('xdata', [], 'ydata', [],'marker','o','markersize', 6, 'tag','b3');        	handles(12) = b2;   		labels = GetFigLabel( nType-1 );        	xlabel(labels(1));        	ylabel(labels(2));         set(handles(7), 'XTick',[0 60 120 180 240 300 360]);         axis([qstart qstop 0.8*min(g_AnaRes(i+1,:)) 1.2*max(g_AnaRes(i+1,:)) ]);      end   end            if nFigNum > 2      nType = get(handles(4),'value');      axes(handles(8));      if nType == 1         CreateMechanismHandles( handles(8),axislimit,xcoordcp,ycoordcp,xbush1,ybush1,xcoordfp,ycoordfp,xbush2,ybush2);      else         i = nType * 2 -3;         line3 = line( 'xdata', g_AnaRes(i,1:ntotal), 'ydata', g_AnaRes(i+1,1:ntotal),'tag','line3','color', 'r');   		b3 = line('xdata', [], 'ydata', [],'marker','o','markersize', 6, 'tag','b3');        	handles(13) = b3;   		labels = GetFigLabel( nType-1 );        	xlabel(labels(1));        	ylabel(labels(2));         set(handles(8), 'XTick',[0 60 120 180 240 300 360]);         axis([qstart qstop 0.8*min(g_AnaRes(i+1,:)) 1.2*max(g_AnaRes(i+1,:)) ]);      end   end            drawnow;   a = findobj('Tag','ORF Cam Analysis Window');      set(a,'Userdata',handles);   case 'None'   figure( findobj('Tag','ORF Cam Analysis Window'));% Bring the figure to front, if it already existscase 'Close'   cam2_orf_cam_ana('Stop');   a = findobj('Tag','ORF Cam Analysis Window');      close(a);case 'Start'   if exist('nAnimationFlag')      if nAnimationFlag == 1         return;      end   end      a = findobj('Tag','ORF Cam Analysis Window');      handles = get(a,'userdata');   set(handles(1),'Enable','off');   set(handles(2),'Enable','off');   set(handles(3),'Enable','off');   set(handles(4),'Enable','off');   set(handles(21),'Enable','off');   set(handles(22),'Enable','off');   set(handles(23),'Enable','off');      nAnimationFlag = 1;   nChangeSpeed = 0;   handles(10) = nAnimationFlag;   set(a,'userdata',handles);   cam2_orf_cam_ana('Animation');   case 'Stop'   if  isempty(findobj('Tag','ORF Cam Analysis Window'))     	return;      end   a = findobj('Tag','ORF Cam Analysis Window');      handles = get(a,'userdata');   nAnimationFlag = 0;   handles(10) = nAnimationFlag;   set(a,'userdata',handles);   set(handles(1),'Enable','on');   set(handles(21),'Enable','on');   set(handles(22),'Enable','on');   set(handles(23),'Enable','on');   nFigNum = get(handles(1),'value');   for i = 1:nFigNum,     	set(handles(i+1),'Enable','on');   end      case 'Return'       %************************ modified to close analysis window ******************   cam2_orf_cam_ana('Stop');%    if ~isempty(findobj('Tag','cam_dsgn'))   %      	b = findobj('Tag','cam_dsgn');%       figure(b);%      	b = findobj('Tag','ORF Cam Analysis Window');   %      	close(b);%    else%   	   return;      %    end   a = findobj('Tag','ORF Cam Analysis Window');      close(a);case 'Get File'% Get the file name using the UIGETFILE   [sFileName sFilePath] = uigetfile('*.dat','Load File');    if sFileName == 0 && sFilePath == 0   	return;   end% Combine the path and file name together   sTemp = strcat( sFilePath, sFileName );% get the size to erase the '.dat' extension    sz = size(sFileName);% load the data file   load(sTemp);% assign the parameter to a variable   Parameter = eval(sFileName(1:(sz(2)-4)));    a = findobj('Tag','Analysis Window');   handles = get(a,'userdata');   if length(Parameter) == 5   	nFigNum = Parameter(1);   	nType1 = Parameter(2);   	nType2 = Parameter(3);   	nType3 = Parameter(4);   	set(handles(1),'Value',nFigNum);      set(handles(2),'Value',nType1);   	set(handles(3),'Value',nType2);   	set(handles(4),'Value',nType3);   	set(a,'userdata',handles);   	DrawAnalysisWindow;   	cam2_orf_cam_ana('ShowFigure');   	cam2_orf_cam_ana('Start');   else   	return;   endcase 'Put File'   [sFileName sFilePath] = uiputfile('*.dat','Save As');    a = findobj('Tag','Analysis Window');   handles = get(a,'userdata');   nFigNum = get(handles(1),'Value');   nType1 = get(handles(2),'Value');   nType2 = get(handles(3),'Value');   nType3 = get(handles(4),'Value');   ParameterData = [nFigNum; nType1; nType2; nType3]; % sTemp = strcat( sFilePath, sFileName)   [sTemp , errMsg] = sprintf('%s%s', sFilePath, sFileName);   [strCmd, errMsg] = sprintf('%s %s %s', 'save', sTemp, ' -ascii ParameterData');   eval(strCmd);end % switch%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	Show UI%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function DrawAnalysisWindow();a = findobj('Tag','ORF Cam Analysis Window');handles = get(a,'userdata');h_fignum = handles(1);h_fig1 = handles(2);h_fig2 = handles(3);h_fig3 = handles(4);nFigNum=get(h_fignum,'Value');position = zeros( 4, 4, 4);position(1,1,:) = [0.2 0.25 0.6 0.6];position(2,1,:) = [0.25 0.65 0.5 0.31];position(2,2,:) = [0.25 0.22 0.5 0.31];position(3,1,:) = [0.1 0.42 0.38 0.31];position(3,2,:) = [0.58 0.65 0.38 0.31];position(3,3,:) = [0.58 0.22 0.38 0.31];%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% % Clearup previous axes first%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%for i = 6:8	if handles(i) ~= 0		delete(handles(i));		handles(i) = 0;	endendfor i = 17:19	if handles(i) ~= 0		delete(handles(i));		handles(i) = 0;	endendfor i = 1:nFigNum,	set(handles(i+1),'Enable','on');end%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% % Axis 1%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% if nFigNum == 1	b = axes('Parent',a, ...	'Box','on', ...	'Color',[1 1 1], ...	'Position',position(1,1,:), ...	'XColor',[0 0 0], ...	'XGrid','off', ...	'XTick',[],...	'YColor',[0 0 0], ...	'YGrid','off', ...	'YTick',[], ...	'ZColor',[0 0 0], ...	'ZGrid','off', ...	'ZTick',[]);handles(17) = b;h_1 = axes('Parent',a, ...	'CameraUpVector',[0 1 0], ...	'CameraUpVectorMode','manual', ...	'Color',[1 1 1], ...	'Position',position(1,1,:), ...	'Tag','Axes1', ...	'XColor',[0 0 0], ...	'YColor',[0 0 0], ...	'ZColor',[0 0 0]);handles(6) = h_1;end	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% % Axis 2%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%if nFigNum == 2	b = axes('Parent',a, ...	'Box','on', ...	'Color',[1 1 1], ...	'Position',position(2,1,:), ...	'XColor',[0 0 0], ...	'XGrid','off', ...	'XTick',[],...	'YColor',[0 0 0], ...	'YGrid','off', ...	'YTick',[], ...	'ZColor',[0 0 0], ...	'ZGrid','off', ...	'ZTick',[]);handles(17) = b;h_1 = axes('Parent',a, ...	'CameraUpVector',[0 1 0], ...	'CameraUpVectorMode','manual', ...	'Color',[1 1 1], ...	'Position',position(2,1,:), ...	'Tag','Axes1', ...	'XColor',[0 0 0], ...	'YColor',[0 0 0], ...   'ZColor',[0 0 0]);b = axes('Parent',a, ...	'Box','on', ...	'Color',[1 1 1], ...	'Position',position(2,2,:), ...	'XColor',[0 0 0], ...	'XGrid','off', ...	'XTick',[],...	'YColor',[0 0 0], ...	'YGrid','off', ...	'YTick',[], ...	'ZColor',[0 0 0], ...	'ZGrid','off', ...	'ZTick',[]);handles(18) = b;h_2 = axes('Parent',a, ...	'CameraUpVector',[0 1 0], ...	'CameraUpVectorMode','manual', ...	'Color',[1 1 1], ...	'Position',position(2,2,:), ...	'Tag','Axes2', ...	'XColor',[0 0 0], ...	'YColor',[0 0 0], ...	'ZColor',[0 0 0]);handles(6) = h_1;handles(7) = h_2;end	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Axis 3%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%if nFigNum == 3	b = axes('Parent',a, ...	'Box','on', ...	'Color',[1 1 1], ...	'Position',position(3,1,:), ...	'XColor',[0 0 0], ...	'XGrid','off', ...	'XTick',[],...	'YColor',[0 0 0], ...	'YGrid','off', ...	'YTick',[], ...	'ZColor',[0 0 0], ...	'ZGrid','off', ...	'ZTick',[]);handles(17) = b;h_1 = axes('Parent',a, ...	'CameraUpVector',[0 1 0], ...	'CameraUpVectorMode','manual', ...	'Color',[1 1 1], ...	'Position',position(3,1,:), ...	'Tag','Axes1', ...	'XColor',[0 0 0], ...	'YColor',[0 0 0], ...	'ZColor',[0 0 0]);b = axes('Parent',a, ...	'Box','on', ...	'Color',[1 1 1], ...	'Position',position(3,2,:), ...	'XColor',[0 0 0], ...	'XGrid','off', ...	'XTick',[],...	'YColor',[0 0 0], ...	'YGrid','off', ...	'YTick',[], ...	'ZColor',[0 0 0], ...	'ZGrid','off', ...	'ZTick',[]);handles(18) = b;h_2 = axes('Parent',a, ...	'CameraUpVector',[0 1 0], ...	'CameraUpVectorMode','manual', ...	'Color',[1 1 1], ...	'Position',position(3,2,:), ...	'Tag','Axes2', ...	'XColor',[0 0 0], ...	'YColor',[0 0 0], ...	'ZColor',[0 0 0]);b = axes('Parent',a, ...	'Box','on', ...	'Color',[1 1 1], ...	'Position',position(3,3,:), ...	'XColor',[0 0 0], ...	'XGrid','off', ...	'XTick',[],...	'YColor',[0 0 0], ...	'YGrid','off', ...	'YTick',[], ...	'ZColor',[0 0 0], ...	'ZGrid','off', ...	'ZTick',[]);handles(19) = b;h_3 = axes('Parent',a, ...	'CameraUpVector',[0 1 0], ...	'CameraUpVectorMode','manual', ...	'Color',[1 1 1], ...	'Position',position(3,3,:), ...	'Tag','Axes3', ...	'XColor',[0 0 0], ...	'YColor',[0 0 0], ...	'ZColor',[0 0 0]);handles(6) = h_1;handles(7) = h_2;handles(8) = h_3;end	set( a, 'Userdata', handles);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	Show UI%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function LocalOpenFig();strFigType = [ ...   	'mechanism           ';...      'displacement graph  ';...      'pressure angle graph'];%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	Main Figure%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%ScreenSize = get(0,'ScreenSize');sx = 520;sy = 440;FigPos = [ ScreenSize(3)-sx-10 ScreenSize(4)-sy-95 sx sy ] ;a = figure('Units','Pixels', ...	'Color',[0.8 0.8 0.8], ...   'Name','ORF Cam Analysis Window', ...    'NumberTitle','off', ...	'Position',FigPos, ... 	'Tag','ORF Cam Analysis Window');	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% % Draw the frame for Figure Selection%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%b = uicontrol('Parent',a, ...	'Units','normalized', ...   'BackgroundColor',[0.752941 0.752941 0.752941], ...   'Position',[0.01731 0.020 0.77 0.1117], ...   'Style','frame', ...	'Tag','Frame2');	b = uicontrol('Parent',a, ...	'Units','normalized', ...   'Position',[0.7808 0.020 0.2038 0.1117], ...   'Style','frame', ...   'Tag','Frame1');%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Menu uicontrols%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% b = uimenu('Parent',a, ...	'Label','Parameter', ...	'Tag','menu_parameter');h_load = uimenu('Parent',b, ...	'Callback','cam2_orf_cam_ana(''GetFile'')', ...	'Label','Load', ...	'Tag','submenu_load');h_save = uimenu('Parent',b, ...	'Callback','cam2_orf_cam_ana(''PutFile'')', ...	'Label','Save', ...	'Tag','submenu_save');%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Setup number of figures%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%b = uicontrol('Parent',a, ...   'Units','normalized', ...	'Position',[0.03 0.07 0.095 0.03911], ... 	'String','No. of figs ', ...	'Style','text', ...	'Tag','StaticText2');h_fignum = uicontrol('Parent',a, ...	'Units','normalized', ...   'Callback','cam2_orf_cam_ana(''ChangeFigureNo'');', ...   'Position',[0.135 0.0985 0.10 0.027], ...   'String',['1';'2';'3'], ...   'Style','popupmenu', ...	'Tag','FigNumber', ... 	'Value',2);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Speed Control%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%b = uicontrol('Parent',a, ...	'Units','normalized', ...   'Position',[0.03 0.02793 0.08 0.03910], ...   'String','Speed ', ...	'Style','text', ... 	'Tag','StaticText2');	h_plus = uicontrol('Parent',a, ...	'Units','normalized', ...   'Callback','cam2_orf_cam_ana(''SetSpeed'',0.707);', ...   'Position',[0.1327 0.02955 0.04038 0.03182], ...    'String','+', ...	'Enable','on', ...	'Tag','Plus_button');b = uicontrol('Parent',a, ...	'Units','normalized', ...   'Callback','cam2_orf_cam_ana(''SetSpeed'',1.414);', ...   'Position',[0.2 0.02955 0.04038 0.03182], ...   'String','-', ... 	'Tag','Minus_button');%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Put label in front of the popup menu item 	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%b = uicontrol('Parent',a, ...	'Units','normalized', ...   'Position',[0.2442 0.07821 0.06262 0.03724], ...   'String','Fig 1', ...	'Style','text', ... 	'Tag','StaticText1'); b = uicontrol('Parent',a, ...	'Units','normalized', ...   'Position',[0.5019 0.08007 0.06262 0.03724], ...   'String','Fig 2', ...	'Style','text', ...	'Tag','StaticText1');b = uicontrol('Parent',a, ...	'Units','normalized', ...   'Position',[0.2442 0.03352 0.06262 0.03724], ...   'String','Fig 3', ...   'Style','text', ...	'Tag','StaticText1');	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Setup the Popup menu contents for Figure Type Selection 	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%h_fig1 = uicontrol('Parent',a, ...	'Units','normalized', ...   'Callback','cam2_orf_cam_ana(''ChangeFigure'',1);', ...   'Position',[0.3077 0.0985 0.19256 0.027], ...   'String',strFigType, ... 	'Style','popupmenu', ...	'Tag','Fig1', ...	'Value',1);h_fig2 = uicontrol('Parent',a, ...	'Units','normalized', ...   'Callback','cam2_orf_cam_ana(''ChangeFigure'',2);', ...   'Position',[0.5692 0.0985 0.19256 0.027], ...   'String',strFigType, ...	'Style','popupmenu', ...	'Tag','Fig2', ...	'Value',2);h_fig3 = uicontrol('Parent',a, ...	'Units','normalized', ...   'Callback','cam2_orf_cam_ana(''ChangeFigure'',3);', ...   'Position',[0.3077 0.0495 0.1926 0.027], ...   'String',strFigType, ...	'Style','popupmenu', ...	'Tag','Fig3', ...	'Value',3);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Start/Stop/Return%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%h_start = uicontrol('Parent',a, ...   'Units','normalized', ...	'CallBack','cam2_orf_cam_ana(''Start'')', ...   'Position',[0.7942 0.07727 0.08577 0.04318], ...   'String','Start', ...	'Tag','start_button');h_stop = uicontrol('Parent',a, ...	'Units','normalized', ...	'CallBack','cam2_orf_cam_ana(''Stop'')', ...	'Position',[0.8854 0.07727 0.08577 0.04318], ...    'String','Stop', ...   'Userdata',1,...	'Tag','stop_button');h_return = uicontrol('Parent',a, ...   'Units','normalized', ...	'CallBack','cam2_orf_cam_ana(''Return'')', ...	'Position',[0.7942 0.02891 0.1773 0.04318], ...    'String','Return', ...   'Tag','return_button');drawnow;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Assign Initial value%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%nAnimationFlag = 0;handles = zeros( 1, 24);handles(1) = h_fignum;handles(2) = h_fig1;handles(3) = h_fig2;handles(4) = h_fig3;handles(5) = 0;handles(10) = nAnimationFlag;handles(15) = 1;% initial animation starting indexhandles(16) = 0.1;% initial timesink valuehandles(21) = h_load;handles(22) = h_save;handles(23) = h_return;handles(24) = h_plus;set( a, 'Userdata', handles);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	String Table%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function [ labels ] = GetFigLabel( nFigType )strTable = cell( 2, 3 );strTable( 1,:) = {'cam angle (deg)';'follower displacement';'follower displacement graph';};strTable( 2,:) = {'cam angle (deg)';'pressure angle (deg)';'pressure angle graph';};labels = strTable( nFigType, : );%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	Calculate the result%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function [res] = CalcResult( rb,r0,r1,r3,rised,direction,ainc,segments,user_data );fact=pi/180;rise=rised*fact;% Compute phi0.phi0=acos((r1^2+r3^2-(rb+r0)^2)/(2*r1*r3));% Compute the angular displacement of the flat-faced follower.  The variable% dtheta will change the resolution of the cam.  Make it smaller for% more accurate results.		dtheta=ainc;if direction=='+'	dtheta=-ainc;endtemp=-dtheta;n=abs(360/dtheta);%**********************************************************************************************************%**********************************************************************************************************% Compute the follower displacement and follower center coordinates % relative to cam.% input data from cam2.mb=user_data(1, 1:segments);ts=user_data(2, 1:segments);te=user_data(3, 1:segments);mot=user_data(4, 1:segments);deflect=user_data(5, 1:segments);hs=user_data(6, 1:segments);he=user_data(7, 1:segments);for i=1:segments    pts=(te(:,i)-ts(:,i))/dtheta;     if pts > 0     %<-----------******************************for j=1:pts	temp=temp+dtheta;	theta(j)=temp;	camangle(j)=abs(theta(j));    	    if mot(i)==1.0            coords=hs(i);%             fd(j)=0;%             fdd(j)=0;%             fddd(j)=0;        elseif mot(i)==2.0            coords=uniform(camangle(j),hs(i),he(i),ts(i),te(i));%             f(num)=ff(1);%             fd(num)=ff(2);%             fdd(num)=ff(3);%             fddd(num)=ff(4);        elseif mot(i)==3.0            coords=harmonic(camangle(j),hs(i),he(i),ts(i),te(i));%             f(num)=ff(1);%             fd(num)=ff(2);%             fdd(num)=ff(3);%             fddd(num)=ff(4);        elseif mot(i)==4.0            coords=cycloidal(camangle(j),hs(i),he(i),ts(i),te(i));%             f(num)=ff(1);%             fd(num)=ff(2);%             fdd(num)=ff(3);%             fddd(num)=ff(4);%%%%%%%%%%%%%%%%%%%%%%%% for "poly7" function  **************************        elseif mot(i)==5.0%                  n=(te(:,i)-ts(:,i))/dtheta;% %                 f(1:n)=0;%                 fd(1:n)=0;%                 fdd(1:n)=0;%                 fddd(1:n)=0;                    fd=0;                    fdd=0;                    fddd=0;%%%%%%%%%%%%%%%%%%%%%%%% "poly7" function  **************************    %        if mot(i)==5.0%             coords=poly7(camangle(j),hs(i),he(i),fd(ts(i)/dtheta+1),fd(te(i)/dtheta+1),...%                 fdd(ts(i)/dtheta+1),fdd(te(i)/dtheta+1),fddd(ts(i)/dtheta+1),fddd(te(i)/dtheta+1),ts(i),te(i));              coords=poly7(camangle(j),hs(i),he(i),fd,fd,...                fdd,fdd,fddd,fddd,ts(i),te(i));%            f(j)=coords(1);                        %        end%********************************************************************         end     % end of "if mot(i)==1.0"    %********************************************************  	f(j)=coords(1);	f_d(j)=f(j)/fact;        end     % end of "for j=1:pts"    if i==1        f_1=f;         f_2=[]; f_3=[]; f_4=[]; f_5=[]; f_6=[]; f_7=[]; f_8=[]; f_9=[]; f_10=[];                theta_1=theta;        theta_2=[]; theta_3=[]; theta_4=[]; theta_5=[];         theta_6=[]; theta_7=[]; theta_8=[]; theta_9=[]; theta_10=[];                camangle_1=camangle;         camangle_2=[]; camangle_3=[]; camangle_4=[]; camangle_5=[];         camangle_6=[]; camangle_7=[]; camangle_8=[]; camangle_9=[]; camangle_10=[];            elseif i==2        %f_1=[];         f_2=f;         f_3=[]; f_4=[]; f_5=[]; f_6=[]; f_7=[]; f_8=[]; f_9=[]; f_10=[];        %theta_1=[];         theta_2=theta;        theta_3=[]; theta_4=[]; theta_5=[];         theta_6=[]; theta_7=[]; theta_8=[]; theta_9=[]; theta_10=[];        %camangle_1=[];         camangle_2=camangle; camangle_3=[]; camangle_4=[]; camangle_5=[];         camangle_6=[]; camangle_7=[]; camangle_8=[]; camangle_9=[]; camangle_10=[];     elseif i==3        %f_1=[]; f_2=[];         f_3=f;         f_4=[]; f_5=[]; f_6=[]; f_7=[]; f_8=[]; f_9=[]; f_10=[];        %theta_1=[]; theta_2=[];         theta_3=theta;        theta_4=[]; theta_5=[];         theta_6=[]; theta_7=[]; theta_8=[]; theta_9=[]; theta_10=[];        %camangle_1=[]; camangle_2=[];         camangle_3=camangle; camangle_4=[]; camangle_5=[];         camangle_6=[]; camangle_7=[]; camangle_8=[]; camangle_9=[]; camangle_10=[];     elseif i==4        %f_1=[]; f_2=[]; f_3=[];         f_4=f;         f_5=[]; f_6=[]; f_7=[]; f_8=[]; f_9=[]; f_10=[];        %theta_1=[]; theta_2=[]; theta_3=[];         theta_4=theta;        theta_5=[]; theta_6=[]; theta_7=[]; theta_8=[]; theta_9=[]; theta_10=[];        %camangle_1=[]; camangle_2=[]; camangle_3=[];         camangle_4=camangle; camangle_5=[];         camangle_6=[]; camangle_7=[]; camangle_8=[]; camangle_9=[]; camangle_10=[];    elseif i==5        %f_1=[]; f_2=[]; f_3=[]; f_4=[];         f_5=f;        f_6=[]; f_7=[]; f_8=[]; f_9=[]; f_10=[];        %theta_1=[]; theta_2=[]; theta_3=[]; theta_4=[];         theta_5=theta;         theta_6=[]; theta_7=[]; theta_8=[]; theta_9=[]; theta_10=[];        %camangle_1=[]; camangle_2=[]; camangle_3=[]; camangle_4=[];         camangle_5=camangle;         camangle_6=[]; camangle_7=[]; camangle_8=[]; camangle_9=[]; camangle_10=[];    elseif i==6        %f_1=[]; f_2=[]; f_3=[]; f_4=[]; f_5=[];         f_6=f;        f_7=[]; f_8=[]; f_9=[]; f_10=[];        %theta_1=[]; theta_2=[]; theta_3=[]; theta_4=[]; theta_5=[];         theta_6=theta; theta_7=[]; theta_8=[]; theta_9=[]; theta_10=[];        %camangle_1=[]; camangle_2=[]; camangle_3=[]; camangle_4=[]; camangle_5=[];         camangle_6=camangle; camangle_7=[]; camangle_8=[]; camangle_9=[]; camangle_10=[];    elseif i==7        %f_1=[]; f_2=[]; f_3=[]; f_4=[]; f_5=[]; f_6=[];         f_7=f;        f_8=[]; f_9=[]; f_10=[];        %theta_1=[]; theta_2=[]; theta_3=[]; theta_4=[]; theta_5=[];         %theta_6=[];         theta_7=theta; theta_8=[]; theta_9=[]; theta_10=[];        %camangle_1=[]; camangle_2=[]; camangle_3=[]; camangle_4=[]; camangle_5=[];         %camangle_6=[];         camangle_7=camangle; camangle_8=[]; camangle_9=[]; camangle_10=[];    elseif i==8        %f_1=[]; f_2=[]; f_3=[]; f_4=[]; f_5=[]; f_6=[]; f_7=[];         f_8=f;        f_9=[]; f_10=[];        %theta_1=[]; theta_2=[]; theta_3=[]; theta_4=[]; theta_5=[];         %theta_6=[]; theta_7=[];         theta_8=theta; theta_9=[]; theta_10=[];        %camangle_1=[]; camangle_2=[]; camangle_3=[]; camangle_4=[]; camangle_5=[];         %camangle_6=[]; camangle_7=[];         camangle_8=camangle; camangle_9=[]; camangle_10=[];    elseif i==9        %f_1=[]; f_2=[]; f_3=[]; f_4=[]; f_5=[]; f_6=[]; f_7=[]; f_8=[];         f_9=f;        f_10=[];        %theta_1=[]; theta_2=[]; theta_3=[]; theta_4=[]; theta_5=[];         %theta_6=[]; theta_7=[]; theta_8=[];         theta_9=theta; theta_10=[];        %camangle_1=[]; camangle_2=[]; camangle_3=[]; camangle_4=[]; camangle_5=[];         %camangle_6=[]; camangle_7=[]; camangle_8=[];         camangle_9=camangle; camangle_10=[];    elseif i==10        %f_1=[]; f_2=[]; f_3=[]; f_4=[]; f_5=[]; f_6=[]; f_7=[]; f_8=[]; f_9=[];         f_10=f;        %theta_1=[]; theta_2=[]; theta_3=[]; theta_4=[]; theta_5=[];         %theta_6=[]; theta_7=[]; theta_8=[]; theta_9=[];         theta_10=theta;        %camangle_1=[]; camangle_2=[]; camangle_3=[]; camangle_4=[]; camangle_5=[];         %camangle_6=[]; camangle_7=[]; camangle_8=[]; camangle_9=[];         camangle_10=camangle;        end    f=[];   % empty the buffer    theta=[];    camangle=[];end     % end of "if pts > 0"  <-----------******************************end     % end of "for i=1:segments"f_d=[f_1 f_2 f_3 f_4 f_5 f_6 f_7 f_8 f_9 f_10]; % data are in degreef=f_d*fact;         % convert to radian to calculate the cam profiletheta=[theta_1 theta_2 theta_3 theta_4 theta_5 theta_6 theta_7 theta_8 theta_9 theta_10];camangle=[camangle_1 camangle_2 camangle_3 camangle_4 camangle_5 camangle_6 camangle_7 ...        camangle_8 camangle_9 camangle_10];%  size_f=size(f)             % check the size of the vectors%  size_theta=size(theta)%  size_cam=size(camangle)          for j=1:n     	tt=theta(j)*fact;	if tt<0; tt=tt+2*pi; end	mu=pi+tt-f(j)-phi0;         % f is in radian  	psi(j)=mu-pi/2;	cm=cos(mu);	sm=sin(mu);	ct=cos(tt);	st=sin(tt);	xf(j)=r1*ct+r3*cm;	yf(j)=r1*st+r3*sm;end%**********************************************************************************************************%**********************************************************************************************************% Reorder the prime points in the order of increasing angle% if cam rotation direction is CCW (+).if direction =='+'	for i=2:1:n		j=n-i+2;		rfollower=sqrt(xf(j)^2+yf(j)^2);		angfoll=atan2(yf(j), xf(j));		xftmp(i)=rfollower*cos(angfoll);		yftmp(i)=rfollower*sin(angfoll);	end	for i=2:1:n		xf(i)=xftmp(i);		yf(i)=yftmp(i);	end	end% Compute the chord anglefor j=1:1:n-1	sigma(j)=atan2((yf(j+1)-yf(j)), (xf(j+1)-xf(j)));	if sigma(j) < 0		sigma(j)=sigma(j)+2*pi;	endendsigma(n)=atan2((yf(1)-yf(n)), (xf(1)-xf(n)));if sigma(n) < 0	sigma(n)=sigma(n)+2*pi;end% Compute the average tangent angle and cam coordinatesgamma(1)=atan2(sin(sigma(1)+pi/2)+sin(sigma(n)+pi/2),...               cos(sigma(1)+pi/2)+cos(sigma(n)+pi/2));xc(1)=xf(1)+r0*cos(gamma(1));yc(1)=yf(1)+r0*sin(gamma(1));for j=2:1:n	gamma(j)=atan2(sin(sigma(j)+pi/2)+sin(sigma(j-1)+pi/2),...               cos(sigma(j)+pi/2)+cos(sigma(j-1)+pi/2));	xc(j)=xf(j)+r0*cos(gamma(j));	yc(j)=yf(j)+r0*sin(gamma(j));endxc(n+1)=xf(1)+r0*cos(gamma(1));yc(n+1)=yf(1)+r0*sin(gamma(1));% Compute the pressure anglelamda=atan2((yf(1)-yc(1)),(xf(1)-xc(1)));pa(1)=acos((cos(psi(1))*cos(lamda)+sin(psi(1))*sin(lamda)))/fact;for i=2:1:n	j=n-i+2;	if direction =='-'		j=i;	end	lamda=atan2((yf(i)-yc(i)),(xf(i)-xc(i)));	pa(j)=acos((cos(psi(j))*cos(lamda)+sin(psi(j))*sin(lamda)))/fact;end% Compute the radius of curvaturefor j=1:1:n	jp1=j+1;	jm1=j-1;	if j==1		a1=[xc(n), yc(n)];		b1=[xc(n+1), yc(n+1)];		a2=b1;		b2=[xc(jp1), yc(jp1)];		sign=(xc(n+1)-xc(n))*(yc(jp1)-yc(n+1))-(xc(jp1)-xc(n+1))*(yc(n+1)-yc(n));			end	if j>1		a1=[xc(jm1), yc(jm1)];		b1=[xc(j), yc(j)];		a2=b1;		b2=[xc(jp1), yc(jp1)];		sign=(xc(j)-xc(jm1))*(yc(jp1)-yc(j))-(xc(jp1)-xc(j))*(yc(j)-yc(jm1));	end	coords=pole(a1,a2,b1,b2);	xcent(j)=coords(1);	ycent(j)=coords(2);	rho(j)=10^10;% If flag =0, compute the true value for rho	if coords(3)==0		rho(j)=sqrt((xc(j)-xcent(j))^2+(yc(j)-ycent(j))^2);	end	% Compute the sign for rho	if sign ~=0;		sign=sign/abs(sign);	end	if sign==0; sign=1; end	rho(j)=rho(j)*sign;end% If rho is large, truncate it for drawing purposesrhom=2*sqrt((max(xc))^2+(max(yc))^2);for j=1:1:n	rhod(j)=rho(j);	if abs(rho(j))>rhom		rhod(j)=rhom*rho(j)/abs(rho(j));	endend% Set up coordinates of bushings and pins.rpivot=rb/10;ninc=20;  npoints=ninc+1;centerx=0;centery=0;coord1=circle2(rpivot,centerx,centery,ninc);% find coordinates of cam pinfor i=1:1:npoints	xcoordcp(i)=coord1(i,1);	ycoordcp(i)=coord1(i,2);	xcoordfp(i)=xcoordcp(i)+r1;	ycoordfp(i)=ycoordcp(i);	end% Find coordinates of cam bushingbinc=ninc/2;coord2=bushing(rpivot,centerx,centery,binc,0);bpoints=ninc/2+16;for i=1:1:bpoints	xbush1(i)=coord2(i,1);	ybush1(i)=coord2(i,2);	xbush2(i)=xbush1(i)+r1;	ybush2(i)=ybush1(i);end% Set up coordinates of follower circle in position 1.nincf=20;  npointf=ninc+1;centerxf=r1+r3*cos(pi-phi0-f(1));centeryf=r3*sin(pi-phi0-f(1));coord=circle2(r0,centerxf,centeryf,nincf);% find coordinates of follower circlefor i=1:1:npointf	xcoordf(i)=coord(i,1);	ycoordf(i)=coord(i,2);end% Find coordinates of follower stemxstem(1)=r1;ystem(1)=0;xstem(2)=centerxf;ystem(2)=centeryf;% Loop to analyze the cam machanism for a full cyclenposn=30;rotinc=floor(n/nposn);  %rotation increment to get nrot positions per rotationk=0;jj=0;for j=1:1:n	jj=jj+1;	Q21=-theta(j);	Q2 = Q21*fact;	ct=cos(Q2);	st=sin(Q2);% Rotate the follower by f(j)	centerxf=r1+r3*cos(pi-phi0-f(j));	centeryf=r3*sin(pi-phi0-f(j));	coord=circle2(r0,centerxf,centeryf,nincf);% find coordinates of follower circle	for i=1:1:npointf		xcoordf(i)=coord(i,1);		ycoordf(i)=coord(i,2);	end	xstem(2)=centerxf;	ystem(2)=centeryf;	for i=1:1:2		k=k+1;		xstema(jj,i)=xstem(i);		ystema(jj,i)=ystem(i);		tempx(k)=xstema(jj,i);		tempy(k)=ystema(jj,i);	end			for i=1:1:npointf		k=k+1;			xfollower(jj,i)=xcoordf(i);		yfollower(jj,i)=ycoordf(i);		tempx(k)=xfollower(jj,i);		tempy(k)=yfollower(jj,i);	end% rotate the cam coordinates by theta(j)	ii=0;	for i=1:1:n		ii=ii+1;		k=k+1;			xca(jj,ii)=xc(i)*ct-yc(i)*st;		yca(jj,ii)=xc(i)*st+yc(i)*ct;			tempx(k)=xca(jj,ii);		tempy(k)=yca(jj,ii);	end	nn=ii;	xca(jj,ii+1)=xc(1)*ct-yc(1)*st;	yca(jj,ii+1)=xc(1)*st+yc(1)*ct;	endntotal = n;jtotal = ii+1;% Set the axis limits for the graphs.  Start with the cam mechanism% position graphwidth = 0.95;height = 0.95;xmina=min(tempx)-0.3*abs(min(tempx));xmaxa=max(tempx)+0.1*abs(max(tempx));ymina=min(tempy)-0.2*abs(min(tempy));ymaxa=max(tempy)+0.2*abs(max(tempy));rangex=xmaxa-xmina;rangey=ymaxa-ymina;xmin=xmina-0.05*rangex;xmax=xmaxa+0.05*rangex;ymin=ymina-0.05*rangey;ymax=ymaxa+0.05*rangey;values=axisadjust(xmin, xmax, ymin, ymax, width, height);xmin=values(1);xmax=values(2);ymin=values(3);ymax=values(4);% Decide the matrix size itotal = max([bpoints ntotal]);rtotal = 19+ntotal*2+npointf*2;res = zeros( rtotal, itotal);i = 0;i = i+1; res( i,1:ntotal ) = camangle;i = i+1; res( i,1:ntotal ) = f_d;i = i+1; res( i,1:ntotal ) = camangle;i = i+1; res( i,1:ntotal ) = pa;i = i+1; res( i,1:ntotal ) = camangle;i = i+1; res( i,1:ntotal ) = rho;i = i+1; res( i,1:bpoints ) = xbush1;i = i+1; res( i,1:bpoints ) = ybush1;i = i+1; res( i,1:npoints ) = xcoordcp;i = i+1; res( i,1:npoints ) = ycoordcp;i = i+1; res( i,1:bpoints ) = xbush2;i = i+1; res( i,1:bpoints ) = ybush2;i = i+1; res( i,1:npoints ) = xcoordfp;i = i+1; res( i,1:npoints ) = ycoordfp;i = i+1; res( i:i+ntotal-1,1:jtotal ) = xca;i = i+ntotal; res( i:i+ntotal-1,1:jtotal ) = yca;xstema = xstema';ystema = ystema';i = i+ntotal; res( i:i+1,1:ntotal ) = xstema;i = i+2; res( i:i+1,1:ntotal ) = ystema;xfollower = xfollower';yfollower = yfollower';i = i+2; res( i:i+npointf-1,1:ntotal ) = xfollower;i = i+npointf; res( i:i+npointf-1,1:ntotal ) = yfollower;i = i+npointf;res( i,1:4 ) = [xmin xmax ymin ymax];res( i,5:10 ) = [bpoints npoints jtotal ntotal rotinc npointf];%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Fig Setting for Special Case%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function [] = CreateMechanismHandles( handle,axislimits,xcoordcp,ycoordcp,xbush1,ybush1,xcoordfp,ycoordfp,xbush2,ybush2) axes(handle);%setup the aspect ratio of the mechanism to 1 set(handle, 'DataAspectRatio',[1 1 1],'Color','none','box','off');axis off;xmin = axislimits(1);xmax = axislimits(2);ymin = axislimits(3);ymax = axislimits(4);axis([xmin xmax ymin ymax]);cam=line('xdata', [], 'ydata' ,[], 'linewidth' ,1, 'color', 'k');roller=line('xdata', [], 'ydata' ,[], 'linewidth' ,1, 'color', 'k');stem=line('xdata', [], 'ydata' ,[], 'linewidth',3,'color', 'k');% cam=animatedline('linewidth' ,1, 'color', 'k');% roller=animatedline('linewidth' ,1, 'color', 'k');% stem=animatedline('linewidth',3,'color', 'k');bush1=line('xdata',[],'ydata',[],'color','r');pin1=line('xdata',[],'ydata',[],'color','r');bush2=line('xdata',[],'ydata',[],'color','r');pin2=line('xdata',[],'ydata',[],'color','r');% draw the pins and bushings.set(pin1,'xdata', xcoordcp, 'ydata',ycoordcp);set(bush1,'xdata', xbush1,'ydata', ybush1);set(pin2,'xdata', xcoordfp, 'ydata',ycoordfp);set(bush2,'xdata', xbush2,'ydata', ybush2);hdls=[ cam roller stem ];set(handle,'userdata',hdls);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Set the mechanism position%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function SetMechanismPosition( links,i,xca,yca,xstema,ystema,xfollower,yfollower)cam = links(1);roller = links(2);stem = links(3);[nn,mm] = size(xca);for j=1:1:mm	xcam(j)=xca(i,j);	ycam(j)=yca(i,j);end[nn,mm] = size(xfollower);for j=1:1:mm	xroller(j)=xfollower(i,j);	yroller(j)=yfollower(i,j);end	for j=1:1:2	xstemn(j)=xstema(i,j);	ystemn(j)=ystema(i,j);end		set(cam,'xdata',xcam,'ydata', ycam);set(roller,'xdata', xroller,'ydata', yroller);set(stem,'xdata', xstemn,'ydata', ystemn);% clearpoints(cam);% addpoints(cam,xcam,ycam);% clearpoints(roller);% addpoints(roller,xroller,yroller);% clearpoints(stem);% addpoints(stem,xstemn,ystemn);